'use strict';
var React = require('react');

var User = React.createClass({
  render: function () {
    var user = this.props.user;
    return (
      <div className="committer row">
        <div className="small-6 columns name">
          {user.name}
        </div>
        <div className="small-2 columns text-center delete">
          {user.stats.deletions}
        </div>
        <div className="small-2 columns text-center add">
          {user.stats.additions}
        </div>
        <div className="small-2 columns text-center">
          {user.stats.deletions - user.stats.additions}
        </div>
      </div>
    );
  }
});

module.exports = User;
